$(document).ready(function () {
    $('#count').on('change', function () {
        var input = $(this);
        var is_name = input.val();
        if (is_name || is_name <= 0) { input.removeClass("invalid").addClass("valid"); } else { input.removeClass("valid").addClass("invalid"); }
    });
    $('#generate').on('click', function () {
        // console.log('generate')
        count = $('#count').val()
        console.log(count)
        req = $.ajax({
            url: '/generate',
            type: 'POST',
            data: { count: count }
        });
        req.done(function (data) {
            if (data.word) {
                // ERROR MESSAGE
                toastr.error('Entered value has to be a number');
            } else {
                $('#holder').text(data.sentence);
                $('#cloud').text(data.path);
                console.log(data.path)
            }


            //console.log('done')
            // if (data.limit) {
            //     // ERROR MESSAGE
            //     toastr.error('The sentence was too long to tweet!  Please generate a shorter one!');
            // } else {
            //     // Display a success toast, with a title
            //     toastr.success('Tweet Successfull!');
            // }



        });
    });
    $('#tweet').on('click', function () {
        console.log('tweet')
        req = $.ajax({
            url: '/tweet',
            type: 'POST',
        });
        req.done(function (data) {
            if (data.limit) {
                // ERROR MESSAGE
                toastr.error('The sentence was too long to tweet!  Please generate a shorter one!');
            } else {
                // Display a success toast, with a title
                toastr.success('Tweet Successfull!');
            }
        });
    });
});